INSERT INTO `wp_wc_admin_notes` VALUES (39,'wc-admin-usage-tracking-opt-in','info','en_US','Aider WooCommerce  amliorer ses services grce au suivi de lutilisation','Rcuprer les donnes dutilisation nous permet damliorer WooCommerce. Votre boutique sera prise en compte lors de lvaluation des nouvelles fonctionnalits et de la qualit dune mise  jour, ou pour dterminer lintrt dune amlioration. Vous pouvez toujours consulter les <a href=\"http://chic-dressing.local/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Paramtres</a> et choisir dinterrompre le partage des donnes. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">En savoir plus</a> sur les donnes que nous collectons.','{}','unactioned','woocommerce-admin','2022-05-10 12:38:38',NULL,0,'plain','',0,0,'info');
