INSERT INTO `wp_wc_admin_notes` VALUES (102,'avalara_q3-2023_noAvaTax','marketing','en_US','Automatically calculate VAT in real time','Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.','{}','pending','woocommerce.com','2023-09-08 09:46:26',NULL,0,'plain','',0,0,'info');
INSERT INTO `wp_wc_admin_notes` VALUES (103,'woo-activation-survey-blockers-22AUG23','info','en_US','How can we help you get that first sale?','Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!','{}','pending','woocommerce.com','2023-09-08 09:46:26',NULL,0,'plain','',0,0,'info');
